export default allValid;
/**
 * In array, you need to check all is true
 *
 * @since 1.4.8
 * @category Condition
 * @param {any[]} value First number
 * @returns {boolean} Returns true or false.
 * @example
 *
 * allValid(true, false)
 * // => false
 */
declare function allValid(value: any[]): boolean;
